import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListDBZComponent {

  @Input()
  public characterList: Character[] = []

  @Output()
  public onDeleteCharacter: EventEmitter<number> = new EventEmitter();

  emitDeleteCharacter(index: number):void{
    console.log('Deleting character', index);
    this.onDeleteCharacter.emit(index);
  }


}
<h4>Listado</h4>
<ul class="list-group">
  <li *ngFor="let character of characterList;
      let i = index;
      let isFirst = first;
      let isLast = last;
      let isEven = even;
      let isOdd = odd;"
      class="list-group-item d-flex justify-content-between align-items-center"
      [ngClass]="{
        'list-group-item-success': isFirst,
        'list-group-item-dark': isLast
        }">
    <div>
      <span class="text-primary"> {{ i + 1 }}. </span>
      <strong>Nombre: </strong>
      <span>{{ character.name}} - </span>
      <strong>Power: </strong>
      <span>{{ character.power }}</span>
    </div>

    <!-- <span>Es el primero: {{ i === 0}}</span>
      <span>Es el último: {{ i === characterList.length - 1}}</span> -->

    <button class="btn btn-danger" (click)="emitDeleteCharacter(i)">X</button>

  </li>
</ul>
