import { Component } from '@angular/core';
import { Character } from '../interfaces/character.interface';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html'
})

export class MainPageDBZComponent {

  public characters: Character[] = [
    {
      name: 'Krillin',
      power: 1000
    },
    {
      name: 'Goku',
      power: 9500
    },
    {
      name: 'Vegeta',
      power: 8000
    }
  ];

  onNewCharacter(character: Character): void {
    this.characters.push(character);
  }

  onDeleteCharacter(index: number): void {
    console.log('Deleting character', index);
    this.characters.splice(index);
  }

}
<h1>DBZ Main Page</h1>
<hr>
<div class="row">
  <div class="col">
    <dbz-list [characterList]="characters" (onDeleteCharacter)="onDeleteCharacter($event)"></dbz-list>
  </div>
  <div class="col">
    <dbz-add-character (onNewCharacter)="onNewCharacter($event)" ></dbz-add-character>
  </div>
</div>
