import { Component } from '@angular/core';
import { DbzService } from '../services/dbz.service';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html'
})

export class MainPageDBZComponent {

  constructor(
    public dbzService: DbzService // Utilizamos el servicio en el constructor hace referencia a la instancia de la clase
  ){}


}
<h1>DBZ Main Page</h1>
<hr>
<div class="row">
  <div class="col">
    <dbz-list [characterList]="dbzService.characters" (onDeleteCharacter)="dbzService.onDeleteCharacter($event)"></dbz-list>
  </div>
  <div class="col">
    <dbz-add-character (onNewCharacter)="dbzService.onNewCharacter($event)" ></dbz-add-character>
  </div>
</div>
